import React from 'react';
import BadgeEstado from '../atoms/BadgeEstado';
import ButtonReserva from '../atoms/ButtonReserva';

const TablaReservas = ({ reservas, clientes, glampings, onEdit, onDelete }) => {
  // Funci贸n auxiliar para mostrar nombre del cliente
  const obtenerNombreCliente = (id) => {
    if (!clientes || !Array.isArray(clientes)) return 'Desconocido';
    const cliente = clientes.find((c) => String(c.id) === String(id));
    return cliente ? cliente.nombre : 'Desconocido';
  };

  // Funci贸n auxiliar para mostrar nombre del glamping
  const obtenerNombreGlamping = (id) => {
    if (!glampings || !Array.isArray(glampings)) return 'Desconocido';
    const glamping = glampings.find((g) => String(g.id) === String(id));
    return glamping ? glamping.nombre : 'Desconocido';
  };

  return (
    <table className="w-full mt-6 text-sm border border-gray-300">
      <thead>
        <tr className="bg-[#182B49] text-white text-left">
          <th className="p-2">Cliente</th>
          <th className="p-2">Glamping</th>
          <th className="p-2">Fecha Inicio</th>
          <th className="p-2">Fecha Fin</th>
          <th className="p-2">Total Pagado</th>
          <th className="p-2">Estado</th>
          <th className="p-2">Acciones</th>
        </tr>
      </thead>
      <tbody>
        {reservas.map((reserva) => (
          <tr key={reserva.id} className="bg-white border-t text-center">
            <td className="p-2">{obtenerNombreCliente(reserva.clienteId)}</td>
            <td className="p-2">{obtenerNombreGlamping(reserva.glampingId)}</td>
            <td className="p-2">{reserva.fechaInicio}</td>
            <td className="p-2">{reserva.fechaFin}</td>
            <td className="p-2">${parseFloat(reserva.totalPagado).toLocaleString()}</td>
            <td className="p-2">
              <BadgeEstado estado={reserva.estado} />
            </td>
            <td className="p-2">
              <div className="flex justify-center gap-2">
                <ButtonReserva
                  texto="Editar"
                  tipo="primary"
                  onClick={() => onEdit(reserva)}
                />
                <ButtonReserva
                  texto="Eliminar"
                  tipo="danger"
                  onClick={() => onDelete(reserva.id)}
                />
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default TablaReservas;
