import React from 'react';
import ModalWrapper from '../atoms/ModalWrapper';
import SelectReserva from '../atoms/SelectReserva';
import ButtonReserva from '../atoms/ButtonReserva';

const ModalesReservas = ({
  reservaSeleccionada,
  onCerrarDetalles,
  onCerrarConfirmar,
  onCerrarEstado,
  onConfirmarEliminar,
  onConfirmarEstado,
  onEstadoChange,
  estadoNuevo,
  modalDetallesAbierto,
  modalConfirmarAbierto,
  modalEstadoAbierto
}) => {
  return (
    <>
      {/* Modal de Detalles */}
      <ModalWrapper isOpen={modalDetallesAbierto} onClose={onCerrarDetalles}>
        <h2>Detalles de la Reserva</h2>
        {reservaSeleccionada ? (
          <div>
            <h3>Reserva #{reservaSeleccionada.id}</h3>
            <p><strong>Cliente:</strong> {reservaSeleccionada.cliente?.nombre}</p>
            <p><strong>Documento:</strong> {reservaSeleccionada.cliente?.documento}</p>
            <p><strong>Email:</strong> {reservaSeleccionada.cliente?.email}</p>
            <p><strong>Teléfono:</strong> {reservaSeleccionada.cliente?.telefono}</p>
            <hr />
            <p><strong>Glamping:</strong> {reservaSeleccionada.glamping?.nombre}</p>
            <p><strong>Capacidad:</strong> {reservaSeleccionada.glamping?.capacidad} personas</p>
            <p><strong>Precio por noche:</strong> ${reservaSeleccionada.glamping?.precioPorNoche?.toLocaleString()}</p>
            <hr />
            <p><strong>Fecha de inicio:</strong> {reservaSeleccionada.fechaInicio}</p>
            <p><strong>Fecha de fin:</strong> {reservaSeleccionada.fechaFin}</p>
            <p><strong>Duración:</strong> {reservaSeleccionada.duracion} días</p>
            <p><strong>Precio total:</strong> ${reservaSeleccionada.precioTotal?.toLocaleString()}</p>
            <p><strong>Total pagado:</strong> ${reservaSeleccionada.totalPagado?.toLocaleString()}</p>
            <p><strong>Estado:</strong> <span className={`estado-badge estado-${reservaSeleccionada.estado}`}>{reservaSeleccionada.estado}</span></p>
          </div>
        ) : (
          <p>No se encontraron datos de la reserva.</p>
        )}
      </ModalWrapper>

      {/* Modal Confirmar Eliminación */}
      <ModalWrapper isOpen={modalConfirmarAbierto} onClose={onCerrarConfirmar}>
        <h2>Confirmar eliminación</h2>
        <p>¿Está seguro de que desea eliminar esta reserva? Esta acción no se puede deshacer.</p>
        <div className="modal-footer">
          <ButtonReserva texto="Eliminar" tipo="danger" onClick={onConfirmarEliminar} />
          <ButtonReserva texto="Cancelar" tipo="secondary" onClick={onCerrarConfirmar} />
        </div>
      </ModalWrapper>

      {/* Modal Cambiar Estado */}
      <ModalWrapper isOpen={modalEstadoAbierto} onClose={onCerrarEstado}>
        <h2>Cambiar estado de reserva</h2>
        <div className="form-group">
          <label htmlFor="cambio-estado">Nuevo estado</label>
          <SelectReserva
            id="cambio-estado"
            value={estadoNuevo}
            onChange={onEstadoChange}
            opciones={[
              { value: 'pendiente', label: 'Pendiente' },
              { value: 'confirmada', label: 'Confirmada' },
              { value: 'cancelada', label: 'Cancelada' }
            ]}
          />
        </div>
        <div className="modal-footer">
          <ButtonReserva texto="Guardar" tipo="success" onClick={onConfirmarEstado} />
          <ButtonReserva texto="Cancelar" tipo="secondary" onClick={onCerrarEstado} />
        </div>
      </ModalWrapper>
    </>
  );
};

export default ModalesReservas;
