import React, { useEffect, useState } from 'react';
import { formatoFechaISO, esMismaFecha, formatearFecha } from '../../utils/fechas';
import '../../styles/reservas.css';


const nombresMeses = [
  'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
  'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
];

const diasSemana = ['Dom', 'Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sáb'];

const CalendarioReservas = ({ reservas = [], onMostrarDetalles }) => {
  const [fecha, setFecha] = useState(new Date());

  useEffect(() => {
    renderizarReservasEnCalendario();
  }, [reservas, fecha]);

  const cambiarMes = (offset) => {
    const nuevaFecha = new Date(fecha.getFullYear(), fecha.getMonth() + offset, 1);
    setFecha(nuevaFecha);
  };

  const irHoy = () => {
    setFecha(new Date());
  };

  const renderizarReservasEnCalendario = () => {
    // Este efecto se encarga del DOM solo si fuese necesario manipulación directa
    // Pero en este caso lo manejamos todo vía JSX
  };

  const obtenerDiasCalendario = () => {
    const año = fecha.getFullYear();
    const mes = fecha.getMonth();

    const primerDia = new Date(año, mes, 1);
    const ultimoDia = new Date(año, mes + 1, 0);

    const diasMesAnterior = primerDia.getDay(); // 0 (Dom) a 6 (Sáb)
    const totalDias = diasMesAnterior + ultimoDia.getDate();
    const totalCeldas = Math.ceil(totalDias / 7) * 7;

    const fechaInicio = new Date(año, mes, 1 - diasMesAnterior);
    const dias = [];

    for (let i = 0; i < totalCeldas; i++) {
      dias.push(new Date(fechaInicio.getFullYear(), fechaInicio.getMonth(), fechaInicio.getDate() + i));
    }

    return dias;
  };

  const esDiaReservado = (dia) => {
    return reservas.some(reserva => {
      const inicio = new Date(reserva.fechaInicio);
      const fin = new Date(reserva.fechaFin);
      return dia >= inicio && dia <= fin;
    });
  };

  const obtenerReservasDelDia = (dia) => {
    return reservas.filter(reserva => {
      const inicio = new Date(reserva.fechaInicio);
      const fin = new Date(reserva.fechaFin);
      return dia >= inicio && dia <= fin;
    });
  };

  return (
    <div className="calendar-container mt-6">
      <h3>Calendario de Reservas</h3>

      <div className="calendar-header">
        <h4>{`${nombresMeses[fecha.getMonth()]} ${fecha.getFullYear()}`}</h4>
        <div className="calendar-controls">
          <button onClick={() => cambiarMes(-1)}>&lt;</button>
          <button onClick={irHoy}>Hoy</button>
          <button onClick={() => cambiarMes(1)}>&gt;</button>
        </div>
      </div>

      <div className="calendar-weekdays">
        {diasSemana.map((dia, index) => (
          <div key={index} className="weekday">{dia}</div>
        ))}
      </div>

      <div className="calendar-grid">
        {obtenerDiasCalendario().map((dia, index) => {
          const esOtroMes = dia.getMonth() !== fecha.getMonth();
          const esHoy = esMismaFecha(dia, new Date());
          const clases = `calendar-day${esOtroMes ? ' other-month' : ''}${esHoy ? ' today' : ''}${esDiaReservado(dia) ? ' reserved' : ''}`;

          return (
            <div className={clases} key={index} data-date={formatoFechaISO(dia)}>
              <div className="day-number" data-weekday={diasSemana[dia.getDay()]}>
                {dia.getDate()}
              </div>

              {obtenerReservasDelDia(dia).map((reserva, idx) => (
                <div
                  key={idx}
                  className={`reservation estado-${reserva.estado}`}
                  onClick={() => onMostrarDetalles(reserva.id)}
                >
                  {reserva.clienteNombre} - {reserva.glampingNombre}
                </div>
              ))}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default CalendarioReservas;
