import React from 'react';

const SelectReserva = ({ id, name, value, onChange, options = [], required = false }) => {
  return (
    <select
      id={id}
      name={name}
      value={value}
      onChange={onChange}
      required={required}
      className="form-control"
    >
      <option value="">Seleccione una opci√≥n</option>
      {options.map((opt) => (
        <option key={opt.value} value={opt.value}>
          {opt.label}
        </option>
      ))}
    </select>
  );
};

export default SelectReserva;
