import React, { useState, useEffect } from 'react';
import InputReserva from '../atoms/InputReserva';
import SelectReserva from '../atoms/SelectReserva';
import ButtonReserva from '../atoms/ButtonReserva';

const ReservaForm = ({ clients, glampings, estados, onSave, initialData, onCancel }) => {
  const [form, setForm] = useState({
    cliente_id: '',
    glamping_id: '',
    fecha_inicio: '',
    fecha_fin: '',
    total_pagado: '',
    estado_id: '1',
  });

  useEffect(() => {
    if (initialData) {
      setForm({
        cliente_id: initialData.cliente_id,
        glamping_id: initialData.glamping_id,
        fecha_inicio: initialData.fecha_inicio,
        fecha_fin: initialData.fecha_fin,
        total_pagado: initialData.total_pagado,
        estado_id: initialData.estado_id,
      });
    }
  }, [initialData]);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (!form.cliente_id || !form.glamping_id || !form.fecha_inicio || !form.fecha_fin) {
      alert('Por favor complete los campos obligatorios');
      return;
    }
    onSave(form);
    if (!initialData) {
      setForm({ cliente_id: '', glamping_id: '', fecha_inicio: '', fecha_fin: '', total_pagado: '', estado_id: '1' });
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-lg space-y-4">
      <h2 className="text-2xl font-bold">Nueva Reserva</h2>
      <SelectReserva
        label="Cliente"
        name="cliente_id"
        value={form.cliente_id}
        onChange={handleChange}
        options={clients.map(c => ({ value: c.id, label: c.nombre }))}
        placeholder="Seleccione un cliente"
      />
      <SelectReserva
        label="Glamping"
        name="glamping_id"
        value={form.glamping_id}
        onChange={handleChange}
        options={glampings.map(g => ({ value: g.id, label: g.nombre }))}
        placeholder="Seleccione un glamping"
      />
      <InputReserva
        label="Fecha de inicio"
        name="fecha_inicio"
        type="date"
        value={form.fecha_inicio}
        onChange={handleChange}
        placeholder="dd/mm/aaaa"
      />
      <InputReserva
        label="Fecha de fin"
        name="fecha_fin"
        type="date"
        value={form.fecha_fin}
        onChange={handleChange}
        placeholder="dd/mm/aaaa"
      />
      <InputReserva
        label="Total pagado"
        name="total_pagado"
        type="number"
        value={form.total_pagado}
        onChange={handleChange}
        placeholder=""
      />
      <SelectReserva
        label="Estado"
        name="estado_id"
        value={form.estado_id}
        onChange={handleChange}
        options={estados.map(e => ({ value: e.id, label: e.nombre }))}
      />
      <div className="flex gap-2">
        <ButtonReserva type="submit" variant="primary">Guardar</ButtonReserva>
        {initialData && <ButtonReserva type="button" variant="secondary" onClick={onCancel}>Cancelar</ButtonReserva>}
      </div>
    </form>
  );
};

export default ReservaForm;
