import React from 'react';
import FiltroReserva from '../molecules/FiltroReserva';

const FiltrosReservas = ({
  clientes,
  glampings,
  filtros,
  onChangeFiltro,
  onLimpiarFiltros,
}) => {
  return (
    <div className="bg-white border rounded shadow-sm p-4 mb-6 grid grid-cols-1 md:grid-cols-4 gap-4">
      <FiltroReserva
        label="Cliente"
        id="clienteId"
        value={filtros.clienteId}
        onChange={onChangeFiltro}
        options={[{ value: '', label: 'Todos' }, ...clientes.map(c => ({ value: c.id, label: c.nombre }))]}
      />

      <FiltroReserva
        label="Glamping"
        id="glampingId"
        value={filtros.glampingId}
        onChange={onChangeFiltro}
        options={[{ value: '', label: 'Todos' }, ...glampings.map(g => ({ value: g.id, label: g.nombre }))]}
      />

      <FiltroReserva
        label="Estado"
        id="estado"
        value={filtros.estado}
        onChange={onChangeFiltro}
        options={[
          { value: '', label: 'Todos' },
          { value: 'Pendiente', label: 'Pendiente' },
          { value: 'Pagado', label: 'Pagado' },
          { value: 'Cancelado', label: 'Cancelado' },
        ]}
      />

      <div className="flex items-end">
        <button
          onClick={onLimpiarFiltros}
          className="bg-gray-300 px-4 py-2 rounded text-sm hover:bg-gray-400 w-full"
        >
          Limpiar
        </button>
      </div>
    </div>
  );
};

export default FiltrosReservas;
