import React from 'react';
import ReservaForm from '../organisms/ReservaForm';
import FiltrosReservas from '../organisms/FiltrosReservas';
import TablaReservas from '../organisms/TablaReservas';
import CalendarioReservas from '../organisms/CalendarioReservas';
import ModalesReservas from '../organisms/ModalesReservas';

const ReservasTemplate = ({
  reservas,
  clientes,
  glampings,
  reservaActual,
  onGuardar,
  onEditar,
  onEliminar,
  onVerDetalle,
  onCambiarEstado,
  onFiltrar,
  onResetFiltro,
  filtros,
  calendario,
  setCalendario,
  alerta,
  setAlerta
}) => {
  return (
    <div className="container">
      <h2>Gesti√≥n de Reservas</h2>

      {alerta?.mensaje && (
        <div className={`alert alert-${alerta.tipo}`}>
          {alerta.mensaje}
        </div>
      )}

      <ReservaForm
        reserva={reservaActual}
        clientes={clientes}
        glampings={glampings}
        onGuardar={onGuardar}
        setAlerta={setAlerta}
      />

      <FiltrosReservas
        clientes={clientes}
        glampings={glampings}
        filtros={filtros}
        onFiltrar={onFiltrar}
        onResetFiltro={onResetFiltro}
      />

      <TablaReservas
        reservas={reservas}
        onEditar={onEditar}
        onEliminar={onEliminar}
        onVerDetalle={onVerDetalle}
        onCambiarEstado={onCambiarEstado}
      />

      <CalendarioReservas
        reservas={reservas}
        onVerDetalle={onVerDetalle}
        calendario={calendario}
        setCalendario={setCalendario}
      />

      <ModalesReservas />
    </div>
  );
};

export default ReservasTemplate;
