import React from 'react';
import FormGroupReserva from './FormGroupReserva';
import SelectReserva from '../atoms/SelectReserva';

const FiltroReserva = ({ label, id, value, onChange, options }) => {
  return (
    <FormGroupReserva label={label} htmlFor={id}>
      <SelectReserva
        id={id}
        value={value}
        onChange={onChange}
        options={[
          { value: '', label: 'Todos' },
          ...options.map(opt => ({
            value: opt.value,
            label: opt.label
          }))
        ]}
      />
    </FormGroupReserva>
  );
};

export default FiltroReserva;
