import React from 'react';
import FormGroupReserva from '../molecules/FormGroupReserva';
import ButtonReserva from '../atoms/ButtonReserva';

const ReservaForm = ({
  reserva= {},
  clientes,
  glampings,
  onChange,
  onSubmit,
  onCancelar,
  modoEdicion
}) => {
  return (
    <div className="form-container">
      <h3>{modoEdicion ? 'Editar Reserva' : 'Nueva Reserva'}</h3>
      <form onSubmit={onSubmit}>
        <input type="hidden" name="id" value={reserva.id || ''} />

        <FormGroupReserva
          label="Cliente"
          name="clienteId"
          type="select"
          value={reserva.clienteId}
          options={clientes.map(c => ({ value: c.id, label: c.nombre }))}
          onChange={onChange}
        />

        <FormGroupReserva
          label="Glamping"
          name="glampingId"
          type="select"
          value={reserva.glampingId}
          options={glampings.map(g => ({ value: g.id, label: g.nombre }))}
          onChange={onChange}
        />

        <FormGroupReserva
          label="Fecha de inicio"
          name="fechaInicio"
          type="date"
          value={reserva.fechaInicio}
          onChange={onChange}
        />

        <FormGroupReserva
          label="Fecha de fin"
          name="fechaFin"
          type="date"
          value={reserva.fechaFin}
          onChange={onChange}
        />

        <FormGroupReserva
          label="Total pagado"
          name="totalPagado"
          type="number"
          value={reserva.totalPagado}
          onChange={onChange}
        />

        <FormGroupReserva
          label="Estado"
          name="estado"
          type="select"
          value={reserva.estado}
          options={[
            { value: 'pendiente', label: 'Pendiente' },
            { value: 'confirmada', label: 'Confirmada' },
            { value: 'cancelada', label: 'Cancelada' }
          ]}
          onChange={onChange}
        />

        <div className="form-actions">
          <ButtonReserva texto={modoEdicion ? 'Actualizar' : 'Guardar'} tipo="success" />
          {modoEdicion && (
            <ButtonReserva texto="Cancelar" tipo="default" onClick={onCancelar} />
          )}
        </div>
      </form>
    </div>
  );
};

export default ReservaForm;
